import * as React from 'react';
import { Text, View, StyleSheet, TextInput } from 'react-native';

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      peso: 0,
      altura: 0,
      imc: function () {
        return (this.peso / (this.altura ** 2))
      }
    }
  }

handlePeso(value){
  this.setState({peso: value})
}

handleAltura(value){
  this.setState({altura: value})
}


calculaImc = _ => {
  const { imc } = this.state
  let mensagem

  if(imc < 18.5){
    mensagem = 'Abaixo do peso'
  }

  else if (imc >= 18.5 || imc < 25 ){
    mensagem = 'Peso normal'
  }


  else if (imc >= 25 || imc < 30 ){
    mensagem = 'Peso normal'
  }

  else if (imc >= 30 ){
    mensagem = 'Obesidade'
  }

  else {
    mensagem = 'ERRO'
  }

  alert(mensagem)
}

  render(){
  return (
    <View style={styles.container}>
      <TextInput onBlur={false} onChangeText={this.handlePeso} placeholder={"Entre com seu peso"}/>
      <TextInput onBlur={false} onChangeText={this.handleAltura} placeholder={"Entre com sua altura"}/>
      <Button title={"CALCULAR"} onPress={this.calculaImc}/>
    </View>
  );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#CDCD',
    alignContent: 'center',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center'
  }
})
