import * as React from 'react';
import { Text, View, StyleSheet, TextInput } from 'react-native';
import { Button } from 'react-native-web';

export default class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      peso: 0,
      altura: 0,
      imc: function () {
        return (this.peso / (this.altura ** 2))
      }
    }
  }

  handleAltura = value => {
    this.setState({altura: value})
  }

handlePeso = value => {
  this.setState({peso: value})
}

  render(){
    return (
      <View style={styles.container}>
        <TextInput style={styles.input} onBlur={false} placeholder={"Entre com seu peso"} onChangeText={this.handlePeso}/>
        <TextInput style={styles.input} onBlur={false} placeholder={"Entre com sua altura"} onChangeText={this.handleAltura}/>
        <Button style={styles.button} title={"CALCULAR"} />
      </View>
    );
    }
  }

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
    alignContent: 'center',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center'
  }, input: {
    padding: 5,
    margin: 10,
    width: '300px'
  }, button: {
    padding: 5,
    margin: 20,
  }
})


